import React from 'react'

const AddressField = (props) => {
    return(
        <div className='px-5 flex flex-col w-full h-1/4 mt-10'>
            <h1 className='h-1/2 w-full text-xl text-prussian-blue font-bold'>{props.name}{props.mandatory && <span className='text-red'>*</span>}</h1>
            <input className={`w-full h-1/2 px-3 text-xl font-bold shadow-input-shadow rounded-sm ${props.userValidInput && 'border-4 border-green'}`} placeholder={props.placeholder}></input>
            {props.userValidInput === false && <h1 className='w-full h-1/3 text-red text-xl italic font-extrabold pt-5 px-5'>Address should contain only letters and digits!</h1>}
        </div>
    );
}

export default AddressField;
