import React from 'react'
import { useHistory } from 'react-router-dom';
import {ROUTES_WITHOUT_HOMEPAGE} from '../constants'


const DESTRUCTURING = process.env.REACT_APP_DESTRUCTURING;
const WEBPAGE_LOGO = process.env.REACT_APP_WEBPAGE_LOGO;

const InfoLanding = (props) => {

    let routing = useHistory();
    const currentLocation = props.location.pathname;
    if(ROUTES_WITHOUT_HOMEPAGE[currentLocation]) return null;

    return(
        <div className='relative inset-x-center-section h-screen-78% w-2/3 bg-athens-gray grid grid-areas-landing-layout-info'>
            <div className='grid grid-in-top-section'/>
            <div className='grid grid-in-middle-section'>
                <div className='flex justify-between items-center ml-10'>
                    <img src={`${DESTRUCTURING}`}
                        className='w-2/3 h-1/2'
                    ></img>
                    <img src={`${WEBPAGE_LOGO}`}
                        className='w-1/3 h-1/2'
                    ></img>
                </div>
            </div>
            <div className='grid grid-in-bottom-section'>
                <div className='flex justify-center'>
                    <button className='w-1/4 h-1/3 text-2xl font-bold text-center bg-school-bus-yellow -mt-10 border-solid border-2 border-dove-gray shadow-button-shadow'>Join Us</button>
                </div>
            </div>
        </div>
    )
}

export default InfoLanding;
